// src/App.js
import React, { useState, useEffect } from 'react';
import axios from 'axios';
import './App.css'; // Import CSS file for styling
import { BrowserRouter as Router, Route, Routes} from 'react-router-dom';
import Home from './components/Home';
import ProductDetail from './components/ProductDetails';

function App() {
    const [file, setFile] = useState(null);
    const [uploading, setUploading] = useState(false);
    const [message, setMessage] = useState('');
    const [uploadedImage, setUploadedImage] = useState(null);
    const [images, setImages] = useState([]);
    const [trends, setTrends] = useState([]);
    const [recommendedProducts, setRecommendedProducts] = useState([]);

    useEffect(() => {
        fetchImages();
        fetchTrends();
    }, []);

    const fetchImages = async () => {
        try {
            const response = await axios.get('http://localhost:5000/images');
            setImages(response.data);
        } catch (error) {
            console.error('Error fetching images:', error);
        }
    };

    const fetchTrends = async () => {
        try {
            const response = await axios.get('http://localhost:5000/trends');
            setTrends(response.data);
        } catch (error) {
            console.error('Error fetching trends:', error);
        }
    };

    const handleFileChange = (e) => {
        const selectedFile = e.target.files[0];
        setFile(selectedFile);
        setUploadedImage(URL.createObjectURL(selectedFile));
    };

    const handleUpload = async () => {
        if (!file) {
            setMessage('Please select a file.');
            return;
        }

        setUploading(true);
        setMessage('');

        const formData = new FormData();
        formData.append('image', file);

        try {
            const res = await axios.post('http://localhost:5000/upload', formData, {
                headers: {
                    'Content-Type': 'multipart/form-data',
                },
            });
            console.log(res.data);
            setMessage('File uploaded successfully!');
            setFile(null);
            setUploadedImage(null);
            fetchImages();
        } catch (err) {
            console.error('Error uploading file:', err);
            setMessage('Error uploading file. Please try again.');
        } finally {
            setUploading(false);
        }
    };

    const handleRecommend = async (trend) => {
        try {
            const response = await axios.post('http://localhost:5000/recommend', { trend });
            setRecommendedProducts(response.data);
        } catch (error) {
            console.error('Error recommending products:', error);
        }
    };

    return (
        <Router>
            <Routes>
                <Route path="/" element={<Home 
                    file={file}
                    setFile={setFile}
                    uploading={uploading}
                    setUploading={setUploading}
                    message={message}
                    setMessage={setMessage}
                    uploadedImage={uploadedImage}
                    setUploadedImage={setUploadedImage}
                    images={images}
                    setImages={setImages}
                    trends={trends}
                    setTrends={setTrends}
                    recommendedProducts={recommendedProducts}
                    setRecommendedProducts={setRecommendedProducts}
                    handleFileChange={handleFileChange}
                    handleUpload={handleUpload}
                    handleRecommend={handleRecommend}
                />} />
                <Route path="/product/:id" element={<ProductDetail images={images} />} />
            </Routes>
        </Router>
    );
}

export default App;
