// src/Home.js
import React from 'react';
import { Link } from 'react-router-dom';
import './Home.css'; // Import CSS file for styling specific to Home component

function Home({
    file,
    setFile,
    uploading,
    setUploading,
    message,
    setMessage,
    uploadedImage,
    setUploadedImage,
    images,
    setImages,
    trends,
    setTrends,
    recommendedProducts,
    setRecommendedProducts,
    handleFileChange,
    handleUpload,
    handleRecommend
}) {
    return (
        <div className="App">
            <header className="App-header">
                <h1>Upload Your Wardrobe Image</h1>
            </header>
            <div className="upload-container">
                <input type="file" onChange={handleFileChange} />
                <button onClick={handleUpload} disabled={uploading}>
                    {uploading ? 'Uploading...' : 'Upload'}
                </button>
            </div>
            {message && <p className="message">{message}</p>}
            {uploadedImage && (
                <div className="preview-container">
                    <h2>Preview</h2>
                    <img src={uploadedImage} alt="Uploaded" className="preview-image" />
                </div>
            )}
            <h2>Uploaded Images</h2>
            <div className="images-container">
                {images.length > 0 ? (
                    images.map((image) => (
                        <div key={image} className="image-item">
                            <Link to={`/product/${image}`} className="image-link">
                                <div className="image-card">
                                    <img
                                        src={`http://localhost:5000/uploads/${image}`}
                                        alt={image}
                                        className="uploaded-image"
                                        onError={(e) => {
                                            e.target.onerror = null;
                                            e.target.src = 'placeholder-image-url';
                                        }}
                                    />
                                    <p className="image-name">{image}</p>
                                </div>
                            </Link>
                        </div>
                    ))
                ) : (
                    <p>No images uploaded yet.</p>
                )}
            </div>
            <h2>Fashion Trends</h2>
            <div className="trends-container">
                {trends.length > 0 ? (
                    trends.map((trend, index) => (
                        <div key={index} className="trend-item">
                            <p>{trend}</p>
                            <button onClick={() => handleRecommend(trend)}>Recommend Products</button>
                        </div>
                    ))
                ) : (
                    <p>No trends available.</p>
                )}
            </div>
            <h2>Recommended Products</h2>
            <div className="products-container">
                {recommendedProducts.length > 0 ? (
                    recommendedProducts.map((product, index) => (
                        <div key={index} className="product-item">
                            <p>{product.name}</p>
                            <img src={product.image_url} alt={product.name} />
                            <p>{product.price}</p>
                        </div>
                    ))
                ) : (
                    <p>No products recommended yet.</p>
                )}
            </div>
        </div>
    );
}

export default Home;
