import React, { useState, useEffect } from 'react';
import axios from 'axios';
import './App.css'; // Import CSS file for styling

function App() {
    const [file, setFile] = useState(null);
    const [uploading, setUploading] = useState(false);
    const [message, setMessage] = useState('');
    const [uploadedImage, setUploadedImage] = useState(null);
    const [images, setImages] = useState([]);
    const [trends, setTrends] = useState([]);
    const [recommendedProducts, setRecommendedProducts] = useState([]);

    useEffect(() => {
        fetchImages();
        fetchTrends();
    }, []);

    const fetchImages = async () => {
        try {
            const response = await axios.get('http://localhost:5000/images');
            setImages(response.data);
        } catch (error) {
            console.error('Error fetching images:', error);
        }
    };

    const fetchTrends = async () => {
        try {
            const response = await axios.get('http://localhost:5000/trends');
            setTrends(response.data);
        } catch (error) {
            console.error('Error fetching trends:', error);
        }
    };

    const handleFileChange = (e) => {
        const selectedFile = e.target.files[0];
        setFile(selectedFile);
        setUploadedImage(URL.createObjectURL(selectedFile));
    };

    const handleUpload = async () => {
        if (!file) {
            setMessage('Please select a file.');
            return;
        }

        setUploading(true);
        setMessage('');

        const formData = new FormData();
        formData.append('image', file);

        try {
            const res = await axios.post('http://localhost:5000/upload', formData, {
                headers: {
                    'Content-Type': 'multipart/form-data',
                },
            });
            console.log(res.data);
            setMessage('File uploaded successfully!');
            setFile(null);
            setUploadedImage(null);
            fetchImages();
        } catch (err) {
            console.error('Error uploading file:', err);
            setMessage('Error uploading file. Please try again.');
        } finally {
            setUploading(false);
        }
    };

    const handleRecommend = async (trend) => {
        try {
            const response = await axios.post('http://localhost:5000/recommend', { trend });
            setRecommendedProducts(response.data);
        } catch (error) {
            console.error('Error recommending products:', error);
        }
    };

    return (
        <div className="App">
            <header className="App-header">
                <h1>Upload Your Wardrobe Image</h1>
            </header>
            <div className="upload-container">
                <input type="file" onChange={handleFileChange} />
                <button onClick={handleUpload} disabled={uploading}>
                    {uploading ? 'Uploading...' : 'Upload'}
                </button>
            </div>
            {message && <p className="message">{message}</p>}
            {uploadedImage && (
                <div className="preview-container">
                    <h2>Preview</h2>
                    <img src={uploadedImage} alt="Uploaded" className="preview-image" />
                </div>
            )}
            <h2>Uploaded Images</h2>
            <div className="images-container">
                {images.length > 0 ? (
                    images.map((image) => (
                        <div key={image} className="image-item">
                            <img
                                src={`http://localhost:5000/uploads/${image}`}
                                alt={image}
                                className="uploaded-image"
                                onError={(e) => {
                                    e.target.onerror = null;
                                    e.target.src = 'placeholder-image-url';
                                }}
                            />
                            <p className="image-name">{image}</p>
                        </div>
                    ))
                ) : (
                    <p>No images uploaded yet.</p>
                )}
            </div>
            <h2>Fashion Trends</h2>
            <div className="trends-container">
                {trends.length > 0 ? (
                    trends.map((trend, index) => (
                        <div key={index} className="trend-item">
                            <p>{trend}</p>
                            <button onClick={() => handleRecommend(trend)}>Recommend Products</button>
                        </div>
                    ))
                ) : (
                    <p>No trends available.</p>
                )}
            </div>
            <h2>Recommended Products</h2>
            <div className="products-container">
                {recommendedProducts.length > 0 ? (
                    recommendedProducts.map((product, index) => (
                        <div key={index} className="product-item">
                            <p>{product.name}</p>
                            <img src={product.image_url} alt={product.name} />
                            <p>{product.price}</p>
                        </div>
                    ))
                ) : (
                    <p>No products recommended yet.</p>
                )}
            </div>
        </div>
    );
}

export default App;
