// src/components/ProductDetail.js
import React, { useEffect, useState } from 'react';
import { useParams } from 'react-router-dom';
import axios from 'axios';
import './productDetails.css'; // Import CSS file for styling specific to ProductDetail component

function ProductDetail() {
    const { id } = useParams();
    const [imageDetails, setImageDetails] = useState({});
    const [recommendedProducts, setRecommendedProducts] = useState([]);

    useEffect(() => {
        fetchImageDetails(id);
    }, [id]);

    const fetchImageDetails = async (imageId) => {
        try {
            const response = await axios.get(`http://localhost:5000/image-details/${imageId}`);
            setImageDetails(response.data);
            fetchRecommendedProducts(response.data.wearCategory);
        } catch (error) {
            console.error('Error fetching image details:', error);
        }
    };

    const fetchRecommendedProducts = async (wearCategory) => {
        try {
            const response = await axios.get(`http://localhost:5000/recommended-products?product_id=${id}`);
            setRecommendedProducts(response.data);
        } catch (error) {
            console.error('Error fetching recommended products:', error);
        }
    };

    return (
        <div className="product-detail-container">
            <div className="image-section">
                <img
                    src={`http://localhost:5000/uploads/${id}`}
                    alt={id}
                    className="detailed-image"
                    onError={(e) => {
                        e.target.onerror = null;
                        e.target.src = 'placeholder-image-url';
                    }}
                />
                <div className="image-details">
                    <h2>{imageDetails.name}</h2>
                    <p>{imageDetails.description}</p>
                    <p>Price: {imageDetails.price}</p>
                    <p>Brand: {imageDetails.brand}</p>
                </div>
            </div>
            <div className="recommended-section">
                <h2>Recommended Products</h2>
                <div className="recommended-products">
                    {recommendedProducts.length > 0 ? (
                        recommendedProducts.map((product, index) => (
                            <div key={index} className="recommended-product-item">
                                <img src={product.image_url} alt={product.ProductName} className="recommended-product-image" />
                                <p>{product.ProductName}</p>
                                <p>{product.Price}</p>
                            </div>
                        ))
                    ) : (
                        <p>No recommended products available.</p>
                    )}
                </div>
            </div>
        </div>
    );
}

export default ProductDetail;
